import React, { Component } from 'react';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faQuoteLeft, } from '@fortawesome/free-solid-svg-icons'
import './App.css';

library.add(faQuoteLeft)

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: [
        { quote: 'Be water, my friend', author: 'Bruce Lee' },
        { quote: 'Do, or Do Not. There is no Try.', author: 'Master Yoda' },
        { quote: 'Be excellent to each other', author: 'Bill & Ted' },
      ],
      currentQuote:  { quote: 'Be water, my friend', author: 'Bruce Lee' },
    }
    this.getNewQuote = this.getNewQuote.bind(this);
    //this.getInitialQuote = this.getInitialQuote.bind(this);
  }

  /*
  getInitialQuote = () => {
    const initialQuote = this.state.quotes[0];
    this.setState({
      currentQuote: initialQuote
    });
    console.table(this.state.currentQuote);
  }
*/

  getNewQuote = () => {
    const random = Math.floor(Math.random() * this.state.quotes.length);
    const newQuote = this.state.quotes[random];
    this.setState = ({
      currentQuote: newQuote
    });
    console.table(currentQuote);

  }



  render() {
    return (
      <div id='wrapper'>
        <div id='quote-box'>
          <div id='quote-text'>
            <FontAwesomeIcon icon='quote-left' className='fa-3x' id='quote-icon' />
            <span id='text' className='text'>{this.state.currentQuote.quote}</span>
          </div>
          <div id='author' className='text'>
            <div> - </div>
            <div>{this.state.currentQuote.author}</div>
          </div>
          <div id='buttons'>
            <div id='tweet' className='button'>Icon</div>
            <button id='new-quote' className='button' onClick={this.getNewQuote}>Get New Quote</button>
          </div>

        </div>
        <div id='credits'>App by Esser</div>
      </div>
    );
  }
}

export default App;
