import React, { Component } from 'react';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faQuoteLeft, } from '@fortawesome/free-solid-svg-icons'
import './App.css';

library.add(faQuoteLeft)

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: [
        { quote: 'Be water, my friend', author: 'Bruce Lee' },
        { quote: 'Do, or Do Not. There is no Try.', author: 'Master Yoda' },
        { quote: 'Be excellent to each other', author: 'Bill & Ted' },
      ],
      currentQuote: {},
    }
    this.getNewQuote = this.getNewQuote.bind(this);
    this.getInitialQuote = this.getInitialQuote.bind(this);
  }

  getInitialQuote = () => {
    const quotes = this.state.quotes;
    console.table(quotes);
    console.log('getInitialQuote: ' + quotes[0]);
    console.table(this.state.currentQuote);
    this.setState({
      curentQuote: quotes[0],
    })
  }

  componentDidMount() {
    this.getInitialQuote();
  }

  getNewQuote = () => {
    const quotes = this.state.quotes;
    console.log(quotes);
    const random = Math.floor(Math.random * quotes.length);
    console.table(this.state.quotes[random]);
    this.setState = ({
      currentQuote: this.state.quotes[random]

    })
    console.table(this.state.currentQuote);

  }



  render() {
    return (
      <div id='wrapper'>
        <div id='quote-box'>
          <div id='quote-text'>
            <FontAwesomeIcon icon='quote-left' className='fa-2x' id='quote-icon' />
            <span id='text' className='text'>Quote {this.state.currentQuote.quote}</span>
          </div>
          <div id='author' className='text'>
            <div> - </div>
            <div>Author: {this.state.currentQuote.author}</div>
          </div>
          <div id='buttons'>
            <div id='tweet' className='button'>Icon</div>
            <button id='new-quote' className='button' onClick={this.getNewQuote}>Get New Quote</button>
          </div>
          <div>by mike</div>
        </div>

      </div>
    );
  }
}

export default App;
